mapper.select:
select  ID,TITLE,CONTENT
       from sys_components
  WHERE  ID=#{id}

select  ID,TITLE,CONTENT
       from sys_components
  WHERE  title=#{title}

mapper.insert:
UPDATE sys_components SET
   title=#{title},
   content=#{content,typeHandler=org.apache.ibatis.type.BlobTypeHandler}
  WHERE  ID=#{id}
